                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          --联盟日志的cell
local UIExtend = RARequire("UIExtend")
local RAAllianceBuyRecordCell = {}
local RARootManager = RARequire("RARootManager")
local RAAllianceUtility = RARequire('RAAllianceUtility')
local Utilitys = RARequire('Utilitys')
local RAStringUtil = RARequire('RAStringUtil')
local html_zh_cn = RARequire('html_zh_cn')
local item_conf = RARequire('item_conf')

function RAAllianceBuyRecordCell:new(o)
    o = o or {} 
    -- o.cellType = 0  --
    setmetatable(o,self)
    self.__index = self    
    return o
end

--刷新数据
function RAAllianceBuyRecordCell:onRefreshContent(ccbRoot)
    --todo
    CCLuaLog("RAAllianceBuyRecordCell:onRefreshContent")

    self.ccbfile = ccbRoot:getCCBFileNode() 
 
    self.mTimeLabel = UIExtend.getCCLabelTTFFromCCB(self.ccbfile,'mTime')
    self.mTimeLabel:setString(Utilitys.formatTime(self.info.time/1000))

    self.mAllianceHistory = UIExtend.getCCLabelHTMLFromCCB(self.ccbfile,'mCellLabel')
    -- self.mAllianceHistory:setDimemsions[]

    local itemData = item_conf[self.info.itemId]
    local itemName = _RALang(itemData.item_name)
    itemName = string.gsub(itemName,"%%","%%%%")
    local desStr = RAStringUtil:fill(html_zh_cn["AllianceShopBuyRecord"],self.info.name,self.info.cost,itemName,self.info.count) 
    self.mAllianceHistory:setString(desStr)
end

return RAAllianceBuyRecordCell